const closeIconBase64 = 'data:image/svg+xml;base64,';
const styleRules = `
body {
  margin: 0;
}
.icon {
  position: fixed;
  right: 0;
  top: 0;
  margin: 18px;
  min-width: 1.5em;
  min-height: 1.5em;
  width: 2.5vh;
  height: 2.5vh;
  background: white;
  border-radius: 50%;
  padding: 24px;
  outline: 0;
  box-shadow: 0 3px 5px -1px rgba(0, 0, 0, 0.2), 0 5px 8px rgba(0, 0, 0, 0.14), 0 1px 14px rgba(0, 0, 0, 0.12);
}
`

export async function showPDFInPopup(contentUrl: string) {
  const res = await fetch(contentUrl)
  const blob = await res.blob()
  const url = URL.createObjectURL(blob)

  window.open(url, '_blank')
}

export function showImageInPopup(contentUrl: string) {
  const styleSheet = document.createElement('style')
  styleSheet.innerHTML = styleRules
  const newWin = window.open('about:blank', '_blank')
  newWin.document.write(`
    <style>
    @page {
      size: A4;
      margin: 0;
    }
    </style>
    <a href="javascript: self.close()">
      <img src="${closeIconBase64}" class="icon" alt="">
    </a>
    <img width="100%" src="${contentUrl}" alt="">
  `)

  newWin.document.head.appendChild(styleSheet)
}
